<?phpinclude_once('classe_connexion.php');include_once('classe_user.php');include_once('fonctions.php');include_once('connexion_vars.php');class Core {	var $isAdmin		= false;	var $evenement_db	= NULL;	var $news_db		= NULL;	var $user;	var $user_info		= NULL;	//var $isAdmin		= false;		/* PREPARATION DU CONTENU DE LA PAGE */	function core(){		ini_set('arg_separator.output', '&amp;');		session_start();		date_default_timezone_set('UTC');		global $evenement_cInfo;		global $news_cInfo;		//$this->evenement_db = new connexion($evenement_cInfo->server ,$evenement_cInfo->user	,$evenement_cInfo->password	,$evenement_cInfo->db);		//$this->news_db		= new connexion($news_cInfo->server 	 ,$news_cInfo->user			,$news_cInfo->password		,$news_cInfo->db);		$this->evenement_db = new connexion($evenement_cInfo['server'],$evenement_cInfo['user'],$evenement_cInfo['password'],$evenement_cInfo['db']);		$this->news_db		= new connexion($news_cInfo['server'],$news_cInfo['user'],$news_cInfo['password'],$news_cInfo['db']);		$this->user 		= new user($this->news_db);		$this->user_info	= $this->user->get_user_info();		if($this->user_info){			$this->isAdmin	= $this->user_info->isAdmin;		}else{			$this->isAdmin	= false;		}	}}?>